import React, { Component  } from "react";
import Background from "./components/Background";
import Login from "./components/Login";
import './index.css'


const adminUser={
  username : "admin",
  password : "admin"
}
class App extends Component {
  constructor(){
    super()
    this.state = {
      username : "",
      password : "",
      error : ""
    }

  }


  Login = state=>{
   // console.log(this.state)
    if(state.username==adminUser.name &&  state.password==adminUser.password){
      this.setState({username:state.username,password:state.password})
      console.log(this.state)


    }
    else{
      this.setState({error:"detaill no match"})
    }
  }
  Logout=state=>{
    //  console.log(state)
        this.setState({username:"",password:""})


    }


  render(){
    return (

      <div className="App">


    <div className="wrapper">
  <div className="wave"></div>
    </div>
    <div className="container">

      <Background name={this.state.username}/>



      <div className="login-container">
        {
          (this.state.username!=="")? (<div>

                                         <h1>Hello {this.state.username}</h1> 
                                          <button className="btn" onClick={this.Logout}>logout</button>
                                     </div>
                                      ): (<Login Login={this.Login} error={this.state.error}/>)
        }
      </div>
      </div>




    </div>
  );
  }
}

export default App;
