import React, { useState } from 'react'
import loginUser from '../model/modelUser';

function Login() {
    const [email,emailState]=useState("")
    const [error,errorState]=useState("")
    const [token,tokenState]=useState("")
    const [password,passwordState]=useState("")

    const submit =async (e)=>{
        e.preventDefault();
        const response= await loginUser({
            email: email,
            password:password

        })
        if(response.token !== "")
                token = response.token
        props.Login({"email":email,"error":error,"token":token,"password":password});

    }



    
    return (
          
          <form  onSubmit={submit} method="post">
                    <h2 className="title">Login </h2>
                    <p><span className='error'>{props.error}</span></p>
    
                    <div className="input-div one focus">

                        <div className="i">
                            <i className="fas fa-user"></i>
                        </div>

                        <div>
                         {/*     <label  htmlFor="user">email</label> */}

                            <input type="text"
                                    id='email'
                                    className="input"
                                    placeholder='Usename'
                                    onChange={e=>emailState(e.target.value)}
                                    value={email}
                            
                            />

                        </div>

                    </div>
    
    
                    <div className="input-div two ">

                        <div className="i">
                            <i className="fas fa-lock"></i>
                        </div>

                        <div>

                            <input type="password"
                            id='password'
                            className="input"
                            placeholder='password'
                            onChange={e=>passwordState(e.target.value)}
                            value={password}
                            />
                                  {/*  <label  htmlFor="pass">password </label>*/}
                        </div>
                    </div>
    
    
                    <a href="index.html">Forgot password?</a>
                    <input type="submit" className="btn" value="Login"/>
    
    
                </form>
    
      )
  }


export default Login
