import React, { useState  } from "react";
import Background from "../../components/Background";
import Login from "../../components/Login"
import '../../index.css'
import logoutUser from "../../components/logout";
import ImageProfile from "../../components/ImageProfile";
import QrScanner from 'qr-scanner';





function LoginPage()  {

  const [email,emailState]=useState("")
  const [error,errorState]=useState("")
  const [token,tokenState]=useState("")
  const [result, setresult] = useState("")


  const submit =()=>{
    const date = new Date();
    let s =date.getFullYear()+":"+date.getMonth()+":"+date.getDay()+":"+date.getHours()+":"+date.getMinutes()+":"+date.getSeconds() 
    console.log(s)
  }


  const Readcode=(e)=>{
    const file = e.target.files[0];
    if (!file) {
        return;
    }
    QrScanner.scanImage(file, { returnDetailedScanResult: true })
        .then(result => console.log(result.data))
        .catch(e => console.log(e));

  }


 


  const Loginfunc = (email_,token_,resp)=>{
    if(token_!==undefined){
      emailState(email_)
      tokenState(token_)



    }
    else{
      console.log(resp[0])
      errorState(resp[0])

    }

  }

  const Logout=state=>{
        logoutUser(token)
        emailState("")
        errorState("")
        tokenState("")
  
      
    }

    return (

      <div >


    <div className="wrapper">
  <div className="wave"></div>
    </div>
    {
      (token!=="")? (<ImageProfile Logout_={Logout} />):(<></>)
    } 

    <div className="container">


      <Background token={token}/>






      <div className="login-container">
        {
          (token!=="")? (<div>
            
                                         <h1>Hello {email}</h1> 
                                         <form  onSubmit={submit}>
                                         <input type="file" onChange={(e)=>Readcode(e)} ></input>
                                          <input type="submit" className="btn" value="Submit" / >
                                          </form>
                                     </div>
                                      ): (<Login Loginfunc={Loginfunc} error={error} errorState={errorState}
                                         emailState={emailState} email={email} tokenState={tokenState} token={token}  />)
        }
                              </div>
      </div>
     



    </div>
  );
}

export default LoginPage;
