import react from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

const adminUser={
  email : "admin",
  password : "admin"
}
class App extends Component {
  constructor(){
    super()
    this.state = {
      email : "",
      password : "",
      error : ""
    }

  }


  Login = state=>{
    console.log(state)
    console.log(adminUser)
    if(state.email===adminUser.email &&  state.password===adminUser.password){
      this.setState({email:state.email,password:state.password})


    }
    else{
      this.setState({error:"detaill no match"})
    }

  }
  Logout=state=>{
    //  console.log(state)
        this.setState({email:"",password:""})


    }


  render(){
    return (

      <div className="App">


    <div className="wrapper">
  <div className="wave"></div>
    </div>
    <div className="container">

      <Background name={this.state.email}/>



      <div className="login-container">
        {
          (this.state.email!=="")? (<div>

                                         <h1>Hello {this.state.email}</h1> 
                                          <button className="btn" onClick={this.Logout}>logout</button>
                                     </div>
                                      ): (<Login Login={this.Login} error={this.state.error}/>)
        }
      </div>
      </div>




    </div>
  );
  }
}

export default App;
