import React, { useState } from 'react'
import loginUser from '../model/modelUser';

function Login(props) {


    const submit =async (e)=>{
        e.preventDefault();
        const response= await loginUser({
            email: props.email,
            password:props.password

        })
       console.log(response.detail)
        if(response.token !== "")
                props.tokenState(response.token)
        
        console.log(props.token)
        props.Loginfunc(props.email,props.token,response.detail);


    }



    
    return (
          
          <form  onSubmit={submit} method="post">
                    <h2 className="title">Login </h2>
                    <p><span className='error'>{props.error}</span></p>
    
                    <div className="input-div one focus">

                        <div className="i">
                            <i className="fas fa-user"></i>
                        </div>

                        <div>
                         {/*     <label  htmlFor="user">email</label> */}

                            <input type="text"
                                    id='email'
                                    className="input"
                                    placeholder='Usename'
                                    onChange={e=>props.emailState(e.target.value)}
                                    value={props.email}
                            
                            />

                        </div>

                    </div>
    
    
                    <div className="input-div two ">

                        <div className="i">
                            <i className="fas fa-lock"></i>
                        </div>

                        <div>

                            <input type="password"
                            id='password'
                            className="input"
                            placeholder='password'
                            onChange={e=>props.passwordState(e.target.value)}
                            value={props.password}
                            />
                                  {/*  <label  htmlFor="pass">password </label>*/}
                        </div>
                    </div>
    
    
                    <a href="/">Forgot password?</a>
                    <input type="submit" className="btn" value="Login"/>
    
    
                </form>
    
      )
  }


export default Login
